import React, { Component } from "react";

class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
            items: [],
            isLoaded: false,
        };
    }

    componentDidMount() {
        fetch("https://excuser-three.vercel.app/v1/excuse/funny/")
            .then((res) => res.json())
            .then((json) => {
                this.setState({
                    isLoaded: true,
                    items: json,
                });
            });
    }

    render() {
        let {isLoaded, items} = this.state;

        if(!isLoaded) {
            return <div>Loading..</div>
        }

        return (
            <div className="App">
                <p>hello</p>
            </div>
        );
    }
}

export default App;
