import React, { Component, useState } from "react";
import "./ExcusesStyle.css";

class Funny extends Component {
    constructor(props) {
        super(props);
        this.state = {
            items: [],
            isLoaded: false,
        };
    }

    componentDidMount() {
        fetch("https://excuser-three.vercel.app/v1/excuse/funny/")
            .then((res) => res.json())
            .then((json) => {
                this.setState({
                    isLoaded: true,
                    items: json,
                });
            });
    }

    render() {
        const { isLoaded, items } = this.state;
        const [str, setStr] = useState

        if (!isLoaded) {
            return (
                <div className="excuseContainer">
                    <div className="excuseInnerContainer">
                        Loading......
                    </div>
                </div>
            );
        } else {
            return (
                <div className="excuseContainer">
                    <div className="excuseInnerContainer">
                        <button className="closeBtn" onClick={() => str = "fuck you"}>X</button>
                        {items.map((item) => (
                            <p key={item.id} className="excuseQuote">
                                {item.excuse}
                            </p>
                        ))}
                        <p>{str}</p>
                    </div>
                </div>
            );
        }
    }
}

export default Funny;
