import React, { Component } from "react";
import Navbar from "./Components/Navbar";
class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
            items: [],
            isLoaded: false,
        };
    }

    componentDidMount() {
        fetch("https://excuser-three.vercel.app/v1/excuse/funny/")
            .then((res) => res.json())
            .then((json) => {
                this.setState({
                    isLoaded: true,
                    items: json,
                });
            });
    }

    render() {
        const { isLoaded, items } = this.state;
        if (!isLoaded) {
            return <div>Loading......</div>;
        } else {
            return (
                <div className="App">
                    <p>Data has been loaded</p>
                    <ul>
                        {items.map((item) => (
                            <li key={item.id}>{item.excuse}</li>
                        ))}
                    </ul>
                <Navbar />
                </div>
            );
        }
    }
}

export default App;
