const [isShown, setIsShown] = useState(false);

    return (
        <div>
            <h2>Choose to generate an excuse.</h2>
            <button className="genExcsBtn" onClick={() => setIsShown(true)}>Generate a Funny Excuse</button>
            <button className="genExcsBtn" onClick={() => setIsShown(true)}> Generate an Excuse for School</button>
            <main>
                {isShown && <Modal setModal={setIsShown}/>}
            </main>
        </div>
    );
}

export default Home;
