import React, { Component } from "react";
import "./ExcusesStyle.css";

class Funny extends Component {
    constructor(props) {
        super(props);
        this.state = {
            items: [],
            isLoaded: false,
        };
    }

    componentDidMount() {
        fetch("https://excuser-three.vercel.app/v1/excuse/funny/")
            .then((res) => res.json())
            .then((json) => {
                this.setState({
                    isLoaded: true,
                    items: json,
                });
            });
    }

    render() {
        const { isLoaded, items } = this.state;
        if (!isLoaded) {
            return (
                <div className="excuseContainer">
                    <div className="excuseInnerContainer">
                        Loading......
                    </div>
                </div>
            );
        } else {
            return (
                <div className="excuseContainer">
                    <div className="excuseInnerContainer">
                        <button className="closeBtn" onClick={this.props.onCancel}>X</button>
                        ite
                        {items.map((item) => (
                            <p key={item.id} className="excuseQuote">
                                {item.excuse}
                            </p>
                        ))}
                    </div>
                </div>
            );
        }
    }
}

export default Funny;
